import React, { Component } from 'react';

class FetchData extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      world: [],
      countries: []
    }
  }

  componentDidMount() {
    fetch("https://api.covid19api.com/summary")
      .then(res => res.json())
      .then(
        (data) => {
          console.log(data.Global)
          this.setState({
            isLoaded: true,
            items: data.Global.global
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
      console.log(this.state.global)
  }

  render() {
    const { error, isLoaded, global} = this.state;

    if (error) {
      return <div className="container"> Error: {error.message} </div>
    } else if (!isLoaded) {
      return <div className="container"> Loading </div>
    } else {
      return(
        <div className="container">
          <ul>
            {global.map(item => (
              <li >
                {item.total_cases}
              </li>
            ))}
          </ul>
        </div>
      );
    } 
  }
}

export default FetchData;
